code.hooks/useFetch.jsx:
import { useState, useEffect } from 'react';
 const useFetch = (url) => {
 const [data, setData] = useState(null);
 const [loading, setLoading] = useState(true);
 useEffect(() => {
 fetch(url)
 .then(res => res.json())
 .then(setData)
 .finally(() => setLoading(false));
 }, [url]);
 return { data, loading };
};
 export default useFetch;

